import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import MazeJourney from './MazeJourney'; // Reuse the MazeJourney component

// Styled-components for layout and form styling
const ChallengeContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(135deg, #e0eafc 0%, #cfdef3 100%);
  text-align: center;
`;

const Title = styled.h2`
  font-size: 2.5rem;
  color: #333;
  margin-bottom: 2rem;
  font-family: 'Poppins', sans-serif;
`;

const Form = styled(motion.form)`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: rgba(255, 255, 255, 0.1);
  padding: 2rem;
  border-radius: 15px;
  backdrop-filter: blur(10px);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
`;

const Input = styled.input`
  width: 250px;
  padding: 0.8rem;
  margin-bottom: 1.5rem;
  border: none;
  border-radius: 25px;
  text-align: center;
  font-size: 1.2rem;
  font-family: 'Poppins', sans-serif;
  outline: none;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const SubmitButton = styled(motion.button)`
  padding: 0.8rem 2rem;
  background-color: #2ecc71;
  color: white;
  border: none;
  border-radius: 50px;
  font-size: 1.2rem;
  font-family: 'Poppins', sans-serif;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #27ae60;
  }
`;

const Challenge4 = () => {
  const [answer, setAnswer] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (answer.toLowerCase() === 'correct answer') {
      navigate('/challenge5');
    } else {
      alert('Incorrect answer. Please try again.');
    }
  };

  return (
    <ChallengeContainer>
      {/* Maze Journey to track progress */}
      <MazeJourney currentStep={4} totalSteps={5} />

      <Title
        as={motion.h2}
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Challenge 4: Solve the Riddle
      </Title>

      <Form
        onSubmit={handleSubmit}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <Input
          type="text"
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          placeholder="Enter your answer"
        />
        <SubmitButton
          type="submit"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Submit
        </SubmitButton>
      </Form>
    </ChallengeContainer>
  );
};

export default Challenge4;
