import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';

const Challenge4 = () => {
  const [faceScanStatus, setFaceScanStatus] = useState('Not Scanned');
  const [photoTaken, setPhotoTaken] = useState(false);
  const navigate = useNavigate();
  const webcamRef = useRef(null);

  const capture = React.useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    // Here you can send `imageSrc` to a backend service to verify the face.
    console.log('Captured photo:', imageSrc);
    setFaceScanStatus('Scanning...');
    setPhotoTaken(true);

    // Simulate face recognition success or failure
    setTimeout(() => {
      if (Math.random() > 0.5) { // Simulated random success or failure
        setFaceScanStatus('Face recognized');
        navigate('/final');
      } else {
        setFaceScanStatus('Face not recognized. Try again.');
      }
    }, 3000); // Simulated delay
  }, [navigate]);

  return (
    <div className="challenge4">
      <h2>Challenge 4: Face Scan</h2>
      <div className="webcam-container">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
        />
      </div>
      <button onClick={capture} disabled={photoTaken}>
        {photoTaken ? 'Photo Taken' : 'Take Photo'}
      </button>
      <p>Status: {faceScanStatus}</p>
    </div>
  );
};

export default Challenge4;
