import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Challenge4 = () => {
  const [faceScan, setFaceScan] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate a frustrating process
    setTimeout(() => {
      if (faceScan === 'verified') {
        navigate('/final');
      } else {
        alert('Face scan failed. Try again.');
      }
    }, 3000); // Simulated delay
  };

  return (
    <div className="challenge4">
      <h2>Challenge 4: Face Scan (with delay)</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={faceScan}
          onChange={(e) => setFaceScan(e.target.value)}
          placeholder="Complete face scan"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Challenge4;
