import React, { useState } from 'react';
import { Button, TextField, Typography, Container, Card, CardContent } from '@mui/material';
import styled from 'styled-components';

const Challenge2Container = styled(Container)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: linear-gradient(135deg, #ffe0b2, #ffab91);
  padding: 2rem;
`;

const ValidationCard = styled(Card)`
  max-width: 400px;
  width: 100%;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-top: 2rem;
`;

const Challenge2 = () => {
  const [formData, setFormData] = useState({ username: '', email: '' });
  const [errors, setErrors] = useState({ username: '', email: '' });

  const validateForm = () => {
    let valid = true;
    const newErrors = { username: '', email: '' };

    if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters long';
      valid = false;
    }

    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Invalid email address';
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Form Submitted:', formData);
    }
  };

  return (
    <Challenge2Container>
      <Typography variant="h4" gutterBottom>
        Challenge 2: Form with Validation
      </Typography>
      <ValidationCard>
        <CardContent>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Username"
              name="username"
              variant="outlined"
              fullWidth
              margin="normal"
              value={formData.username}
              onChange={handleChange}
              error={!!errors.username}
              helperText={errors.username}
            />
            <TextField
              label="Email"
              name="email"
              variant="outlined"
              fullWidth
              margin="normal"
              value={formData.email}
              onChange={handleChange}
              error={!!errors.email}
              helperText={errors.email}
            />
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Submit
            </Button>
          </form>
        </CardContent>
      </ValidationCard>
    </Challenge2Container>
  );
};

export default Challenge2;
