import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import {
  startRegistration,
  startAuthentication,
  isWebAuthnAvailable,
} from '@simplewebauthn/browser';

const Challenge4 = () => {
  const [faceScanStatus, setFaceScanStatus] = useState('Not Scanned');
  const [fingerprintStatus, setFingerprintStatus] = useState('Not Scanned');
  const [photoTaken, setPhotoTaken] = useState(false);
  const [authenticating, setAuthenticating] = useState(false);
  const navigate = useNavigate();
  const webcamRef = useRef(null);

  // Capture Face
  const captureFace = React.useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    console.log('Captured photo:', imageSrc);
    setFaceScanStatus('Scanning...');
    setPhotoTaken(true);

    // Simulate face recognition success or failure
    setTimeout(() => {
      if (Math.random() > 0.5) { // Simulated random success or failure
        setFaceScanStatus('Face recognized');
        startFingerprintAuth(); // Proceed to fingerprint authentication
      } else {
        setFaceScanStatus('Face not recognized. Try again.');
        setPhotoTaken(false);
      }
    }, 3000); // Simulated delay
  }, []);

  // Fingerprint Authentication
  const startFingerprintAuth = async () => {
    if (!isWebAuthnAvailable()) {
      alert('WebAuthn is not available in your browser.');
      return;
    }

    setAuthenticating(true);
    try {
      const response = await startAuthentication({
        // You should fetch these options from your server
        publicKey: {
          // Replace with actual publicKey object received from server
        },
      });
      console.log('Fingerprint authentication response:', response);

      // Simulate authentication success
      setTimeout(() => {
        if (response.success) { // Replace with actual response check
          setFingerprintStatus('Fingerprint recognized');
          navigate('/final');
        } else {
          setFingerprintStatus('Fingerprint not recognized. Try again.');
        }
        setAuthenticating(false);
      }, 3000); // Simulated delay
    } catch (error) {
      console.error('Error during fingerprint authentication:', error);
      setFingerprintStatus('Authentication error. Try again.');
      setAuthenticating(false);
    }
  };

  return (
    <div className="challenge4">
      <h2>Challenge 4: Face Scan and Fingerprint Authentication</h2>
      <div className="webcam-container">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
        />
      </div>
      <button onClick={captureFace} disabled={photoTaken}>
        {photoTaken ? 'Photo Taken' : 'Take Photo'}
      </button>
      <p>Status: {faceScanStatus}</p>
      {photoTaken && (
        <div>
          <h3>Fingerprint Authentication</h3>
          <button onClick={startFingerprintAuth} disabled={authenticating}>
            {authenticating ? 'Authenticating...' : 'Authenticate Fingerprint'}
          </button>
          <p>Status: {fingerprintStatus}</p>
        </div>
      )}
    </div>
  );
};

export default Challenge4;
