import React, { useState } from 'react';
import { Button, Typography, Container, Card, CardContent } from '@mui/material';
import { useSpring, animated } from 'react-spring';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const Challenge3Container = styled(Container)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: linear-gradient(135deg, #d1c4e9, #b39ddb);
  padding: 2rem;
`;

const AuthCard = styled(Card)`
  max-width: 400px;
  width: 100%;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-top: 2rem;
`;

const AnimatedButton = styled(animated(Button))`
  margin: 1rem;
`;

const Challenge3 = () => {
  const [authStatus, setAuthStatus] = useState('Not Authenticated');
  const [authenticating, setAuthenticating] = useState(false);
  const navigate = useNavigate();

  // Animation for button
  const buttonProps = useSpring({
    opacity: 1,
    from: { opacity: 0 },
    reset: true,
    config: { duration: 500 }
  });

  const handleAuthenticate = () => {
    setAuthenticating(true);
    setTimeout(() => {
      setAuthStatus('Authenticated');
      navigate('/next');
      setAuthenticating(false);
    }, 2000); // Simulated authentication delay
  };

  return (
    <Challenge3Container>
      <Typography variant="h4" gutterBottom>
        Challenge 3: Authentication Flow
      </Typography>
      <AuthCard>
        <CardContent>
          <Typography variant="h6">
            Status: {authStatus}
          </Typography>
          <AnimatedButton
            variant="contained"
            color="primary"
            onClick={handleAuthenticate}
            disabled={authenticating}
            style={buttonProps}
          >
            {authenticating ? 'Authenticating...' : 'Authenticate'}
          </AnimatedButton>
        </CardContent>
      </AuthCard>
    </Challenge3Container>
  );
};

export default Challenge3;
